mapper.insert:
insert into lcc_payment(product_idx,price,quantity,total,product_name , user_id, recipient_name,recipient_phone ,payment_method ,payment_company ,payment_number
, payment_amount,payment_status ,recipient_addr ,recipient_addrnum ,order_state )

values (#{product_idx},#{price},#{quantity},#{total}
,#{product_name} ,#{user_id} ,#{recipient_name} ,#{recipient_phone} ,#{payment_method} ,#{payment_company} ,#{payment_number} ,#{payment_amount} ,#{payment_status}
,#{recipient_addr} ,#{recipient_addrnum} ,#{order_state} )

mapper.update:
update lcc_payment
set payment_status=#{payment_status},
order_state = #{order_state}
where payment_idx=#{payment_idx}
